import React, { useState, useEffect, useRef } from "react";
import styled, { keyframes } from "styled-components";
import { FaCogs, FaTools, FaProjectDiagram } from "react-icons/fa";

const fadeInUp = keyframes`
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
`;

const Wrapper = styled.div`
  font-family: 'Segoe UI', sans-serif;
  background-color: #ffffff;
  color: #222222;
`;

const AnimatedSection = styled.div`
  opacity: 0;
  transform: translateY(20px);
  transition: all 0.6s ease-out;

  &.visible {
    opacity: 1;
    transform: translateY(0);
  }
`;

const Navbar = styled.nav`
  display: flex;
  justify-content: space-between;
  align-items: center;
  background-color: #222222;
  color: white;
  padding: 1rem 2rem;
`;

const NavLinks = styled.div`
  display: flex;
  gap: 1.5rem;

  a {
    color: white;
    text-decoration: none;
    font-weight: bold;
  }
`;

const LanguageSwitcher = styled.button`
  background: none;
  border: none;
  color: white;
  font-weight: bold;
  cursor: pointer;
`;

const Hero = styled.section`
  background-color: #2d70b5;
  color: white;
  padding: 6rem 2rem;
  text-align: center;
  animation: ${fadeInUp} 1s ease-out;
`;

const HeroTitle = styled.h1`
  font-size: 2.5rem;
  margin-bottom: 1rem;
`;

const HeroSubtitle = styled.p`
  font-size: 1.25rem;
`;

const Section = styled.section`
  padding: 4rem 2rem;
  max-width: 1000px;
  margin: 0 auto;
`;

const SectionTitle = styled.h2`
  font-size: 2rem;
  margin-bottom: 2rem;
  color: #2d70b5;
`;

const ServicesList = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: 2rem;
`;

const ServiceCard = styled.div`
  border: 1px solid #ddd;
  border-radius: 8px;
  padding: 1.5rem;
  background-color: #f9f9f9;
  text-align: center;
  transition: transform 0.3s ease, box-shadow 0.3s ease;

  &:hover {
    transform: translateY(-5px);
    box-shadow: 0 8px 20px rgba(0, 0, 0, 0.1);
  }

  h3 {
    margin-top: 1rem;
  }
`;

const IconWrapper = styled.div`
  font-size: 2rem;
  color: #2d70b5;
`;

const CTASection = styled.section`
  background-color: #4db3f7;
  color: white;
  padding: 4rem 2rem;
  text-align: center;
`;

const ContactForm = styled.form`
  display: flex;
  flex-direction: column;
  gap: 1rem;
  max-width: 400px;
  margin: 2rem auto 0;
`;

const Input = styled.input`
  padding: 0.75rem;
  border: none;
  border-radius: 4px;
  font-size: 1rem;
`;

const Textarea = styled.textarea`
  padding: 0.75rem;
  border: none;
  border-radius: 4px;
  font-size: 1rem;
  min-height: 100px;
`;

const SubmitButton = styled.button`
  padding: 0.75rem;
  background-color: #222222;
  color: white;
  border: none;
  border-radius: 4px;
  font-weight: bold;
  cursor: pointer;
`;

function useScrollAnimation() {
  const ref = useRef();

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          entry.target.classList.add("visible");
        }
      },
      { threshold: 0.1 }
    );

    if (ref.current) {
      observer.observe(ref.current);
    }

    return () => {
      if (ref.current) observer.unobserve(ref.current);
    };
  }, []);

  return ref;
}

export default function HomePage() {
  const [lang, setLang] = useState("nl");

  const content = {
    nl: {
      nav: ["Home", "Diensten", "Contact"],
      heroTitle: "Jouw workflow, zo geregeld",
      heroSubtitle: "Wij helpen kleine bedrijven met Microsoft 365 Business Basic",
      servicesTitle: "Onze Diensten",
      services: [
        {
          title: "Microsoft 365 Setup",
          desc: "Wij zorgen voor een snelle en professionele implementatie van Microsoft 365 voor jouw bedrijf.",
          icon: <FaCogs />
        },
        {
          title: "Onderhoud & Support",
          desc: "Technische ondersteuning en onderhoud om jouw bedrijfsprocessen soepel te laten verlopen.",
          icon: <FaTools />
        },
        {
          title: "Workflow Optimalisatie",
          desc: "Efficiëntere processen en betere samenwerking met de tools van Microsoft 365.",
          icon: <FaProjectDiagram />
        }
      ],
      ctaTitle: "Klaar om te starten?",
      ctaSubtitle: "Neem vandaag nog contact met ons op voor een gratis intakegesprek.",
      contactButton: "Verstuur"
    },
    en: {
      nav: ["Home", "Services", "Contact"],
      heroTitle: "Your workflow, sorted",
      heroSubtitle: "We help small businesses with Microsoft 365 Business Basic",
      servicesTitle: "Our Services",
      services: [
        {
          title: "Microsoft 365 Setup",
          desc: "We ensure a fast and professional implementation of Microsoft 365 for your business.",
          icon: <FaCogs />
        },
        {
          title: "Maintenance & Support",
          desc: "Technical support and maintenance to keep your workflows running smoothly.",
          icon: <FaTools />
        },
        {
          title: "Workflow Optimization",
          desc: "Streamline your processes and collaboration with Microsoft 365 tools.",
          icon: <FaProjectDiagram />
        }
      ],
      ctaTitle: "Ready to get started?",
      ctaSubtitle: "Contact us today for a free consultation.",
      contactButton: "Send"
    }
  };

  const t = content[lang];
  const servicesRef = useScrollAnimation();
  const contactRef = useScrollAnimation();

  return (
    <Wrapper>
      <Navbar>
        <div>ICT Flow</div>
        <NavLinks>
          {t.nav.map((label, index) => (
            <a href={`#${label.toLowerCase()}`} key={index}>{label}</a>
          ))}
        </NavLinks>
        <LanguageSwitcher onClick={() => setLang(lang === "nl" ? "en" : "nl")}>{lang === "nl" ? "EN" : "NL"}</LanguageSwitcher>
      </Navbar>

      <Hero id="home">
        <HeroTitle>{t.heroTitle}</HeroTitle>
        <HeroSubtitle>{t.heroSubtitle}</HeroSubtitle>
      </Hero>

      <AnimatedSection ref={servicesRef} id="diensten">
        <Section>
          <SectionTitle>{t.servicesTitle}</SectionTitle>
          <ServicesList>
            {t.services.map((service, index) => (
              <ServiceCard key={index}>
                <IconWrapper>{service.icon}</IconWrapper>
                <h3>{service.title}</h3>
                <p>{service.desc}</p>
              </ServiceCard>
            ))}
          </ServicesList>
        </Section>
      </AnimatedSection>

      <AnimatedSection ref={contactRef} id="contact">
        <CTASection>
          <h2>{t.ctaTitle}</h2>
          <p>{t.ctaSubtitle}</p>
          <ContactForm onSubmit={(e) => { e.preventDefault(); alert('Message sent!'); }}>
            <Input type="text" placeholder="Name" required />
            <Input type="email" placeholder="Email" required />
            <Textarea placeholder="Your message" required />
            <SubmitButton type="submit">{t.contactButton}</SubmitButton>
          </ContactForm>
        </CTASection>
      </AnimatedSection>
    </Wrapper>
  );
}
